(3065, '_widget_w-linkboxlist-5_css_class', 'field_64f1a97cb531b', 'off'),
(3069, 'widget_w-linkboxlist-5_fc_list_content_0_fc_icon', '482', 'off'),
(3070, '_widget_w-linkboxlist-5_fc_list_content_0_fc_icon', 'field_66bb8443ab0d8', 'off'),
(3071, 'widget_w-linkboxlist-5_fc_list_content_0_fc_title', 'Leaking Cartridges', 'off'),
(3072, '_widget_w-linkboxlist-5_fc_list_content_0_fc_title', 'field_66bb8459ab0d9', 'off'),
(3073, 'widget_w-linkboxlist-5_fc_list_content_0_fc_content', 'If you notice that your cartridge is leaking, start by examining it for any signs of physical damage. Next, ensure all cartridge parts are securely connected by tightening them without overdoing it. To prevent future leaks, monitor the liquid level in the chamber and avoid overfilling.\r\n\r\nIn addition, make sure that the holes in the cartridge are not clogged. Regularly clean your cartridge and ensure that it is free of debris to help prevent leaks and maintain optimal performance.', 'off'),
(3074, '_widget_w-linkboxlist-5_fc_list_content_0_fc_content', 'field_66bb848cab0da', 'off'),
(3075, 'widget_w-linkboxlist-5_fc_list_content_1_fc_icon', '481', 'off'),
