(43, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-08-09 01:57:50', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-08-08 01:57:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-08 01:58:05', NULL, 0, 'plain', '', 0, 1, 'info'),
(59, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-09 01:57:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-09 01:57:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-12 00:59:14', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-12 00:59:14', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2024-08-13 01:57:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wnww-recap-july', 'marketing', 'en_US', 'Powerful updates and upgrades in WooCommerce', 'Have you seen all the latest from Woo? Check out our recent roundup for a look at the newest features, extensions, and insights making WooCommerce perform better than ever.', '{}', 'pending', 'woocommerce.com', '2024-08-14 01:59:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2024-08-14 12:16:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Try the new way to get paid', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooPayments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>', '{}', 'unactioned', 'woocommerce-admin', '2024-08-15 03:18:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'wc-update-db-reminder', 'update', 'en_US', 'Yêu cầu cập nhật cơ sở dữ liệu WooCommerce', 'WooCommerce đã được cập nhật! Để đảm bảo mọi thứ hoạt động trơn tru, chúng tôi cần cập nhật cơ sở dữ liệu lên bản mới nhất. Quá trình cập nhật cơ sở dữ liệu sẽ chạy nền và sẽ mất chút thời gian, hãy kiên nhẫn. Người dùng cao cấp có thể cập nhật thay thế qua <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', '{}', 'unactioned', 'woocommerce-core', '2024-08-27 08:46:20', NULL, 0, 'plain', '', 1, 1, 'info'),
(69, 'bfcm2024-landing-page', 'marketing', 'en_US', 'The best way to prepare for holiday success', 'The upcoming holiday season is the busiest time of the year for many online stores. We want to help you make the most of it with an early gift: our first-ever Black Friday Cyber Monday Trends Report. It\'s full of stats, trends, and recommendations to knock your holiday socks off!', '{}', 'pending', 'woocommerce.com', '2024-09-05 01:58:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'stripe_securityupdate_q3_2024', 'update', 'en_US', 'Security update required for your Stripe extension', 'To continue securely accepting payments, you must update your Stripe extension to at least version 8.6, re-authenticate, and turn on the new checkout experience. Review the documentation for instructions on how to make the necessary changes from your WooCommerce dashboard.', '{}', 'pending', 'woocommerce.com', '2024-09-23 02:35:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'paypal_migration_Q3_2024', 'marketing', 'en_US', 'You’re using an outdated version of PayPal', 'Don\'t miss out on potential sales this holiday season — upgrade your store\'s checkout experience by switching to the all-in-one PayPal Payments extension. Enable customers to pay using Venmo and Pay Later (where available), credit and debit cards, Apple Pay, Google Pay, and country-specific options.', '{}', 'pending', 'woocommerce.com', '2024-10-04 01:57:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-13 01:15:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2024-12-20 07:14:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'account-creation-after-checkout-dec2024', 'marketing', 'en_US', 'Offer account creation after checkout', 'Your customers can now create accounts after they check out on block-based themes. Enable the new “After checkout” block in your account settings and enjoy smoother guest checkout, better conversion, and more repeat business.', '{}', 'pending', 'woocommerce.com', '2024-12-20 07:14:20', NULL, 0, 'plain', '', 0, 0, 'info');
